import { useEffect, useState } from "react"
import Navbar from "./Navbar"
import Sidebar from "./Sidebar"
import axios from "axios"
import toast from 'react-toastify'
function Ord(){
   const url='http://localhost:4001'
   const [email,setEmail]=useState('')
   const [orders,setOrders]=useState([])
    useEffect(()=>{
       
},[])

    const handleC=()=>{

      const res= axios.post(`${url}/corders`,{email:email})
       if (res.status===200){
         toast('hello')
           setOrders(res.data)

       }
    }
   return(
    <>
    <Navbar/>
 <div  className="inline-flex w-full" >
      <Sidebar  />
      <div  className="w-150  gap-4">
      <p className=" text-sm font-semibold ml-5 ">enter email</p>
        <input  type='text' placeholder="email" className=" text-sm ml-10  rounded-md  text-left  border-emerald-800 
        border-7 border" onChange={(e)=>{setEmail(e.target.value)}}></input>
     <hr></hr><br></br>
   <button className="text-sm bg-black text-white p-2
   rounded-md ml-10 hover:scale-105" onClick={handleC}> Find Orders</button>
</div>
<div className="w-full border-2 text-sm ">
    <h4 className="text-center justify-center ">Orders</h4>
     { orders && orders.map((item,i)=>(<div className="inline-flex gap-10">
        <p>{item.quantity}</p>
     </div>))}
</div>
</div>
    </>
   )

}

export default Ord
