import axios from 'axios'
import { useEffect,useState } from "react"
import Navbar from "./Navbar"
import Sidebar from "./Sidebar"
function List({url}){
    const [orders,setOrders]=useState([])
    const [email,setEmail]=useState('')
    const flist=async ()=>{
        const res= await axios.post(`${url}/corders`,{email:email})
            if(res.status===200){
              setOrders(res.data)
           
           }}
     useEffect(()=>{
          flist()
        },[])
    const handleC=()=>{
        alert(orders.length)
    }
    return(<>
     <Navbar/>
 <div  className="inline-flex w-full" >
      <Sidebar  />
      <div  className="w-150 inline-block gap-2">
      <p className=" text-sm font-semibold ml-5 ">enter email</p>
        <input  type='text' placeholder="email" className=" text-sm ml-10  rounded-md  text-left  border-emerald-800 
        border-7 border" onChange={(e)=>{setEmail(e.target.value)}}></input>
        <hr className='mb-4 mt-2'></hr>
  <button  className='bg-black text-white text-sm  hover:scale-105
  p-2 rounded-md ml-8' onClick={handleC} >Find Orders</button>
      </div>
 {  orders.length>0 && <><div className="w-full border-2 text-sm ">
    <h4 className="text-center justify-center ">Orders</h4>
     {orders && orders.map((item,i)=>(<div className="inline-flex gap-10">
        <p>{item.quantity}</p>
     </div>))}
</div></>
 }
</div>
</>)
}

export default List
