import Navbar from "./Navbar"
import Sidebar from "./Sidebar"
import axios from "axios"
function Ord(){
   const url='http://localhost:4001'
   const [email,setEmail]=useState('')
   const [orders,setOrders]=useState([])
    useEffect(()=>{
       const res= axios.post(`${url}/corders`,{email:email})
       if (res.status===200){
           setOrders(res.data.data)
       }
},[])

    const handleC=()=>{

    }
   return(
    <>
    <Navbar/>
 <div  className="inline-flex w-full" >
      <Sidebar  />
      <div  className="w-100 inline-block gap-4">
      <p className=" text-sm ml-5 ">enter email</p>
        <input  type='text' placeholder="email" className=" text-sm ml-10 w-8/12 rounded-md  text-left  border-emerald-800 
        border-7 border" onChange={(e)=>{setEmail(e.target.value)}}></input>
   <button className="text-sm bg-black text-white p-2 rounded-md ml-10"> Find Orders</button>
</div>
<div className="w-300 border-2 auto-cols-fr">
    <h4 className="text-center justify-center ">Orders</h4>
     {orders.map((item,i)=>(<div className="inline-flex gap-10">
        <p>{item.quantity}</p>
     </div>))}
</div>
</div>
    </>
   )

}

export default Ord
